import React, { useState } from 'react';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Auth from './components/Auth.js';

function AppContent() {
  const [currentView, setCurrentView] = useState('home');
  const [selectedStartup, setSelectedStartup] = useState(null);
  const { loading } = useAuth();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-50 to-blue-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <h2 className="text-2xl font-semibold text-gray-700">Loading StartupHub...</h2>
        </div>
      </div>
    );
  }

  const handleStartupClick = (startup) => {
    setSelectedStartup(startup);
    setCurrentView('startup-detail');
  };

  const renderView = () => {
    switch (currentView) {
      case 'auth':
        return <Auth onSuccess={() => setCurrentView('home')} />;
      case 'startup-detail':
        return (
          <div className="max-w-4xl mx-auto p-8 text-center">
            <div className="bg-white rounded-2xl shadow-lg p-8">
              <div className="text-6xl mb-4">{selectedStartup?.logo}</div>
              <h1 className="text-3xl font-bold mb-4">{selectedStartup?.name}</h1>
              <p className="text-gray-600 mb-6">{selectedStartup?.description}</p>
              <button 
                onClick={() => setCurrentView('home')}
                className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors"
              >
                ‚Üê Back to Startups
              </button>
            </div>
          </div>
        );
      case 'jobs':
        return (
          <div className="max-w-4xl mx-auto p-8 text-center">
            <div className="bg-white rounded-2xl shadow-lg p-8">
              <div className="text-6xl mb-4">üíº</div>
              <h1 className="text-3xl font-bold mb-4">Jobs View</h1>
              <p className="text-gray-600">Coming Soon...</p>
            </div>
          </div>
        );
      case 'profile':
        return (
          <div className="max-w-4xl mx-auto p-8 text-center">
            <div className="bg-white rounded-2xl shadow-lg p-8">
              <div className="text-6xl mb-4">üë§</div>
              <h1 className="text-3xl font-bold mb-4">Profile View</h1>
              <p className="text-gray-600">Coming Soon...</p>
            </div>
          </div>
        );
      default:
        return <Home onStartupClick={handleStartupClick} />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50">
      <Navbar currentView={currentView} onViewChange={setCurrentView} />
      {renderView()}
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

export default App;
